import { GoogleLogin } from 'react-google-login'
import axios from 'axios'
import { useState } from 'react'

function GoogleCalendar() {

  const responseGoogle = response => {
    console.log(response);
    console.log("wwww");
    const { code } = response;
    axios.post('/api/create-tokens', { code })
      .then(response => {
        console.log(response.data);
        setSignedIn(true);
      })
      .catch(error => console.log(error.message));

  }

  const responseError = (error) => {
    console.log("sss");
    console.log(error);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(summary, description, location, endDateTime, startDateTime);
    axios.post('/api/create-event', {
      summary,
      description,
      location,
      endDateTime,
      startDateTime,
    })
    .then(response => {
      console.log(response.data);
    })
    .catch(error => console.log(error.message));

  }

  const [summary, setSummary] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');
  const [endDateTime, setEndDateTime] = useState('');
  const [startDateTime, setStartDateTime] = useState('');
  const [signedIn, setSignedIn] = useState(false);

  return (
    <>
      <div>
        <h1>Google Calendar</h1>
        <p>This is the Google Calendar page</p>
      </div>
      {
        !signedIn ? (
          <div>
            <GoogleLogin
              clientId='586680212360-rtc6o4nre05vi4fl9v53iuq2cdng1ai9.apps.googleusercontent.com'
              buttonText="sign in and authorize calendar"
              onSuccess={responseGoogle}
              onFailure={responseError}
              cookiePolicy={'single_host_origin'}
              responseType='code'
              accessType='offline'
              scope='openid email profile https://www.googleapis.com/auth/calendar'
            />
          </div>
        ) : (
          <div>
            <form onSubmit={handleSubmit}>
              <label htmlfor="summary">Summary</label>
              <br />
              <input
                type="text"
                id="summary"
                defautlValue={summary}
                onChange={e => setSummary(e.target.value)} />
              <br />

              <label htmlfor="description">description</label>
              <br />
              <textarea
                type="text"
                id="description"
                defautlValue={description}
                onChange={e => setDescription(e.target.value)} />
              <br />

              <label htmlfor="location">location</label>
              <br />
              <input
                type="text"
                id="location"
                defautlValue={location} 
                onChange={e => setLocation(e.target.value)} />
              <br />

              <label htmlfor="startDateTime">Start date time</label>
              <br />
              <input
                type="datetime-local"
                id="startDateTime"
                defautlValue={startDateTime}
                onChange={e => setStartDateTime(e.target.value)} />
              <br />

              <label htmlfor="endDateTime">end date time</label>
              <br />
              <input
                type="datetime-local"
                id="endDateTime"
                defautlValue={endDateTime}
                onChange={e => setEndDateTime(e.target.value)} />
              <br />
              <button type="submit">create event</button>
            </form>
          </div>
        )
      }

    </>
  )
}

export default GoogleCalendar;
