import { Card, Button } from 'react-bootstrap';
import logo1 from "../assets/exchange.svg";
import { useEffect, useState, Component } from 'react';
import Axios from 'axios';
import Dropdown from 'react-dropdown';
import { HiSwitchHorizontal } from 'react-icons/hi';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-dropdown/style.css';

function CurrencyWidget() {

  const [info, setInfo] = useState([]);
  const [input, setInput] = useState(0);
  const [from, setFrom] = useState("usd");
  const [to, setTo] = useState("eur");
  const [options, setOptions] = useState([]);
  const [output, setOutput] = useState(0);

  useEffect(() => {
    Axios.get(
`https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/${from}.json`)
   .then((res) => {
      setInfo(res.data[from]);
    })
  }, [from]);

  useEffect(() => {
    setOptions(Object.keys(info));
    convert();
  }, [info])

  function convert() {
    var rate = info[to];
    setOutput(input * rate);
  }

  function flip() {
    var temp = from;
    setFrom(to);
    setTo(temp);
  }
  return (
    <div >
      <Card style={{ width: '39rem', height: '20rem' , backgroundColor: '#ececec'}}>
        <Card.Img variant="top-left" src={logo1} width="50"
          height="50" align="left"/>
        <Card.Body>
          <Card.Title>Currency</Card.Title>
          <div className="middle">
            <Dropdown options={options} 
                      onChange={(e) => { setFrom(e.value) }}
                      value={from}/>
          </div>
          <div className="right">
            <Dropdown options={options} 
                      onChange={(e) => {setTo(e.value)}} 
                      value={to}/>
          </div>
          <div>
            <br />
            <div>
              <input type="text" 
                     placeholder="Enter the amount" 
                     onChange={(e) => setInput(e.target.value)} />
            </div>
            <br />
            <button onClick={()=>{convert()}}>Convert</button>
            <br />
            <br />
            <p>{input+" "+from+" = "+output.toFixed(2) + " " + to}</p>
          </div>
        </Card.Body>
      </Card>
    </div>
  )

  return (
    <div className="CurrencyWidget">
      <div className="heading">
        <h1>Currency converter</h1>
      </div>
      <div className="container">
        <div className="left">
          <h3>Amount</h3>
          <input type="text" 
             placeholder="Enter the amount" 
             onChange={(e) => setInput(e.target.value)} />
        </div>
        <div className="middle">
          <h3>From</h3>
          <Dropdown options={options} 
                    onChange={(e) => { setFrom(e.value) }}
          value={from} placeholder="From" />
        </div>
        <div className="switch">
          <HiSwitchHorizontal size="30px" 
                        onClick={() => { flip()}}/>
        </div>
        <div className="right">
          <h3>To</h3>
          <Dropdown options={options} 
                    onChange={(e) => {setTo(e.value)}} 
          value={to} placeholder="To" />
        </div>
      </div>
      <div className="result">
        <button onClick={()=>{convert()}}>Convert</button>
        <h2>Converted Amount:</h2>
        <p>{input+" "+from+" = "+output.toFixed(2) + " " + to}</p>

      </div>
    </div>
  );
}

export default CurrencyWidget;
